      this.loadRegistrationRequests();
      this.loadAuditLogs();
      this.loadIoTDevices();
      this.loadShelves();
      // Vérifier périodiquement les nouvelles demandes
      setInterval(() => {
        this.loadRegistrationRequests();
      }, 30000);
      // Initialiser les graphiques après le chargement
      setTimeout(() => {
        this.initCharts();
      }, 1000);
    }
    initCharts() {
      if (typeof document !== 'undefined') {
        this.createBorrowsChart();
        this.createCategoriesChart();
        this.createUsersChart();
        this.createTrendsChart();
      }
    }
    createBorrowsChart() {
      const canvas = document.getElementById('borrowsChart');
      if (!canvas) return;
      const ctx = canvas.getContext('2d');
      if (!ctx) return;
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.fillStyle = '#007bff';
      ctx.font = '12px Arial';
      // Utiliser les vraies données
      const totalBooks = this.books.length;
      const totalUsers = this.users.length;
      const activeLoans = this.borrows.filter(loan => loan.status === 'active').length;
      const returnedLoans = this.borrows.filter(loan => loan.status === 'returned').length;
      const overdueLoans = this.overdueBorrows.length;
      const categories = this.categories.length;
      const data = [totalBooks, totalUsers, activeLoans, returnedLoans, overdueLoans, categories];
      const labels = ['Livres', 'Users', 'Actifs', 'Retournés', 'Retards', 'Catég.'];
      const maxValue = Math.max(...data, 1); // Éviter division par 0
      for (let i = 0; i < data.length; i++) {
        const barHeight = data[i] / maxValue * 150;
        const x = i * 45 + 20;
        const y = 180 - barHeight;
        ctx.fillRect(x, y, 35, barHeight);
        ctx.fillStyle = '#333';
        ctx.fillText(labels[i], x, 195);
        ctx.fillText(data[i].toString(), x + 10, y - 5);
        ctx.fillStyle = '#007bff';
      }
    }
    createCategoriesChart() {
      const canvas = document.getElementById('categoriesChart');
      if (!canvas) return;
      const ctx = canvas.getContext('2d');
      if (!ctx) return;
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      if (this.categories.length === 0) {
        ctx.fillStyle = '#666';
        ctx.font = '14px Arial';
        ctx.fillText('Aucune catégorie', 100, 100);
        return;
      }
      const colors = ['#007bff', '#28a745', '#ffc107', '#dc3545', '#6f42c1'];
      const total = this.categories.length;
      let currentAngle = 0;
      const centerX = 150;
      const centerY = 100;
      const radius = 80;
      for (let i = 0; i < this.categories.length; i++) {
        const percentage = 1 / total * 100;
        const sliceAngle = percentage / 100 * 2 * Math.PI;
        ctx.beginPath();
        ctx.moveTo(centerX, centerY);
        ctx.arc(centerX, centerY, radius, currentAngle, currentAngle + sliceAngle);
        ctx.closePath();
        ctx.fillStyle = colors[i % colors.length];
        ctx.fill();
        // Légende
        ctx.fillStyle = colors[i % colors.length];
        ctx.fillRect(20, 20 + i * 20, 15, 15);
        ctx.fillStyle = '#333';
        ctx.font = '10px Arial';
        ctx.fillText(`${this.categories[i].name}`, 40, 32 + i * 20);
        currentAngle += sliceAngle;
      }
    }
    createUsersChart() {
      const canvas = document.getElementById('usersChart');
      if (!canvas) return;
      const ctx = canvas.getContext('2d');
      if (!ctx) return;
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.strokeStyle = '#28a745';
      ctx.lineWidth = 3;
      const data = [5, 8, 12, 18, 25, 28];
      const maxValue = Math.max(...data);
      ctx.beginPath();
      for (let i = 0; i < data.length; i++) {
        const x = i * 50 + 30;
        const y = 180 - data[i] / maxValue * 150;
        if (i === 0) {
          ctx.moveTo(x, y);
        } else {
          ctx.lineTo(x, y);
        }
        // Points
        ctx.fillStyle = '#28a745';
        ctx.beginPath();
        ctx.arc(x, y, 4, 0, 2 * Math.PI);
        ctx.fill();
        // Valeurs
        ctx.fillStyle = '#333';
        ctx.font = '10px Arial';
        ctx.fillText(data[i].toString(), x - 5, y - 10);
      }
      ctx.strokeStyle = '#28a745';
      ctx.stroke();
    }
    createTrendsChart() {
      const canvas = document.getElementById('trendsChart');
      if (!canvas) return;
      const ctx = canvas.getContext('2d');
      if (!ctx) return;
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      // Graphique de tendance simple
      const emprunts = [15, 22, 18, 25, 30, 28];
      const retours = [12, 18, 20, 22, 25, 26];
      // Ligne emprunts
      ctx.strokeStyle = '#007bff';
      ctx.lineWidth = 2;
      ctx.beginPath();
      for (let i = 0; i < emprunts.length; i++) {
        const x = i * 45 + 30;
        const y = 180 - emprunts[i] / 35 * 150;
        if (i === 0) ctx.moveTo(x, y);else ctx.lineTo(x, y);
      }
      ctx.stroke();
      // Ligne retours
      ctx.strokeStyle = '#28a745';
      ctx.beginPath();
      for (let i = 0; i < retours.length; i++) {
        const x = i * 45 + 30;
        const y = 180 - retours[i] / 35 * 150;
        if (i === 0) ctx.moveTo(x, y);else ctx.lineTo(x, y);
      }
      ctx.stroke();
      // Légende
      ctx.fillStyle = '#007bff';
      ctx.fillRect(20, 20, 15, 3);
      ctx.fillStyle = '#333';
      ctx.font = '12px Arial';
      ctx.fillText('Emprunts', 40, 30);
      ctx.fillStyle = '#28a745';
      ctx.fillRect(20, 35, 15, 3);
      ctx.fillText('Retours', 40, 45);
    }
    setActiveTab(tab) {
