    // Méthodes pour les livres
    searchBooks() {
      this.filterBooks();
    }
    filterBooks() {
      this.filteredBooks = this.books.filter(book => {
        const matchesSearch = !this.bookSearch || book.title.toLowerCase().includes(this.bookSearch.toLowerCase()) || book.author.toLowerCase().includes(this.bookSearch.toLowerCase()) || book.isbn.includes(this.bookSearch);
        const matchesCategory = !this.bookCategoryFilter || book.category_id == this.bookCategoryFilter;
        const matchesAvailability = !this.bookAvailabilityFilter || this.bookAvailabilityFilter === '1' && book.stock > 0 || this.bookAvailabilityFilter === '0' && book.stock <= 0;
        return matchesSearch && matchesCategory && matchesAvailability;
      });
    }
